
public class Break_Statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<=10;i++)
		{
			if(i==5)
			{
				break;
			}
			System.out.println(i);
		}
		
		System.out.println("______________________");
		
		for(int a=10;a>=1;a--)
		{
			if(a==6)
			{
				break;
			}
		System.out.println(a);
		}
	
	
	}

}
