
public class Continue_Statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=10;i>=1;i--)
		{
			if(i==5)
			{
				continue;
			}
		System.out.println(i);
		}
		
		System.out.println("_________________");
		
		for(int a=1;a<=10;a++)
		{
			if(a==5)
			{
				continue;
			}
		System.out.println(a);
		}
	}

}
