
public class Do_While_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1;
		
		do
		{
			System.out.println(i);
			i++;
		}while(i<=10);
		
		System.out.println("--------------------");
		
		int a=1;
		do 
		{
			System.out.println(a);
			a+=2;
		}while(a<=10);
	
		System.out.println("--------------------");
		
			
		}
		
	}


