
public class For_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=10;i>=1;i--)
		{
			System.out.println(i);
		}
		
		System.out.println("--------------------");
		
		for(int a=1;a<=10;a++)
		{
			System.out.println(a);
		}
		
	}

}
