
public class Largest_Of_Two_Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=30;
		int b=20;
		
		if(a>b)
		{
			System.out.println("A is Larger than B");
		}
		else
		{
			System.out.println("B is Larger than A");
		}
		
	}

}
