
public class While_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=1;
		
		while(a<=10)
		{
			System.out.println(a);
			a++;
		}
		
		System.out.println("----------------");
		
		int i=2;
		
		while(i<=10)
		{
			System.out.println(i);
			i+=2;
		}
				
			
	}

}
